import fs from 'fs';
import path from 'path';
import {fileURLToPath} from 'url';

/** This script renames type declaration files generated by `vue-tsc` from
 * e.g. `ElementName.vue.d.ts` to `ElementName.d.ts` so that they match
 * generated component names of e.g. `ElementName.ts`.
 */
const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

const root = `${__dirname}/..`;

function traverseRename(directoryPath) {
  const files = fs.readdirSync(directoryPath);

  for (const file of files) {
    const filePath = path.join(directoryPath, file);
    const stat = fs.statSync(filePath);
    if (stat.isFile()) {
      if (file.endsWith('.vue.d.ts')) {
        fs.renameSync(
          filePath,
          `${filePath.replace('.vue.d.ts', '.d.ts')}`,
          (err) => {
            console.log(err);
          }
        );
      }
    } else if (stat.isDirectory()) {
      traverseRename(filePath); // traverse the sub directory and rename files
    }
  }
}

traverseRename(root);
